def preprocess_title_content(title, content):
    # 内容预处理
    content = re.sub(r'\n+', "\n", content)
    content = re.sub(r'\.{6,}', ".", content)
    content = re.sub(r'!+', "!", content)
    content = re.sub(r'！+', "！", content)
    content = re.sub(r'~+', "~", content)
    # 移除特殊符号 <U+0097> <97>
    content = re.sub(r'<\w{2}>', "", content)

    # 移除特殊符号 <U+0097>
    format_content = ''
    for char in content:
        if '\u0080' <= char <= '\u009F' or char == '\u0080':
            print(char)
            continue
        else:
            format_content += char
    content = format_content
    return title, content


